// Pegar os dados do Input
const items = $input.all();
const item = items[0].json;
// Debug - veja o que est√° vindo
console.log('item:', JSON.stringify(item));
let agentes = [];
const user_query = item.user_query;
try {
    // Tentar v√°rias formas de obter os agentes
    if (typeof item.agents === 'string') {
        // Se for string, fazer parse
        const parsed = JSON.parse(item.agents);
        agentes = parsed.agentes || [];
    } else if (item.agents && item.agents.agentes) {
        // Se j√° for objeto
        agentes = item.agents.agentes;
    } else if (item.output) {
        // Tentar campo output
        const parsed = JSON.parse(item.output);
        agentes = parsed.agentes || [];
    }
} catch (e) {
    console.log('Erro ao fazer parse:', e.message);
    // Default: usar todos os agentes
    agentes = ['legislativo', 'politico', 'fiscal'];
}
return agentes.map(agente => ({
    json: {
        agente: agente,
        user_query: user_query
    }
}));
